svg.script:
    function funpolygon(n, polyid){
        var tipud="";
	var varv="fill:#";
	for (var i=0; i<n;i++){
	  var tipud= tipud+Math.floor((Math.random()*128)+1)+",";
	  var tipud= tipud+Math.floor((Math.random()*128)+1)+" ";
	  }
	for (var i=0; i<6;i++){
	  var lisa= (Math.floor((Math.random()*16)));
	  var varv=varv+(lisa.toString(16));
	  }
	varv=varv+"; fill-opacity:"+Math.random()+";";
	var polyg = document.getElementById(polyid);
	polyg.setAttribute('points',tipud);
	polyg.setAttribute('style',varv);
	}
    funpolygon(3, "poly1");
    funpolygon(3, "poly2");
    funpolygon(3, "poly3");
    funpolygon(4, "poly4");
    funpolygon(4, "poly5");
